// ─── Helpers ─────────────────────────────────────────────────────────────────

const parseViewBox = (vb: string): { w: number; h: number } => {
  const parts = vb.trim().split(/\s+/).map(Number);
  if (
    parts.length === 4 &&
    isFinite(parts[2]) &&
    isFinite(parts[3]) &&
    parts[2] > 0 &&
    parts[3] > 0
  ) {
    return { w: parts[2], h: parts[3] };
  }
  return { w: 24, h: 24 };
};

const toNumberIfPossible = (v: number | string | undefined): number | null => {
  if (v === undefined) return null;
  if (typeof v === 'number') return isFinite(v) ? v : null;
  const n = Number(v);
  return isFinite(n) ? n : null;
};

/**
 * Prefix all SVG id="…" attributes and their url(#…) / href="#…" references
 * with a unique per-instance string, so multiple instances on the same page
 * don't share gradient / filter / clipPath ids.
 */
const prefixSvgIds = (svg: string, prefix: string): string => {
  // Prefix id="..." attributes
  let out = svg.replace(
    /\bid="([^"]+)"/g,
    (_m, id: string) => `id="${prefix}${id}"`,
  );

  // Rewrite url(#id) fill/filter/clip/mask references
  out = out.replace(
    /url\(#([^)]+)\)/g,
    (_m, id: string) => `url(#${prefix}${id})`,
  );

  // Rewrite href="#id" and xlink:href="#id" (but never hex colours)
  out = out.replace(
    /(href|xlink:href)="#([^"]+)"/g,
    (_m, attr: string, id: string) => {
      const isHexColor =
        /^[0-9A-Fa-f]{3}$|^[0-9A-Fa-f]{6}$|^[0-9A-Fa-f]{8}$/.test(id);
      if (isHexColor) return `${attr}="#${id}"`;
      return `${attr}="#${prefix}${id}"`;
    },
  );

  return out;
};

const makeSafePrefix = (name: string, uid: string): string =>
  name.replace(/[^a-zA-Z0-9_]/g, '_') + '__' + uid + '__';

